import PokeCard from "../Card/PokeCard";
import style from "./ContainerCards.module.css";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { getAllPokemons} from "../../Redux/actions"
import SearchBar from "../Search/SearchBar";
import Filters from "../Filters/Filters";
import { Link } from "react-router-dom";
import bigpikachu from "../../assets/bigpikachu.jpg";


const ContainerCards = () => {
    const pokemons = useSelector((state) => state.pokemons);
    const dispatch = useDispatch();
    const [currentPage, setCurrentPage] = useState(0);
    const cardsForPage = 12

    const pageCount = Math.ceil(pokemons?.length / cardsForPage);

    const paginated = pokemons?.slice(
      currentPage * cardsForPage,
      (currentPage + 1) * cardsForPage
    );
    const handleNextClick = () => {
      if (currentPage < pageCount - 1) {
        setCurrentPage(currentPage + 1);
      }
    };
  
    const handlePrevClick = () => {
      if (currentPage > 0) {
        setCurrentPage(currentPage - 1);
      }
    };

    return (
        <div>
        <div>
          <div className={style.navBar} >
          <Link to={"/create"}> <button className={style.createPokemon} >CREATE NEW POKEMON</button></Link>
          <Filters setCurrentPage={setCurrentPage} className= {style.filter} />
          <SearchBar setCurrentPage={setCurrentPage} />
          </div>
           
            {paginatedCards.length ?
          <div className={style.container}>
             
             { paginatedCards.map(pokemon => {
                return <div className={style.pokemonsCards} >
                  <Link to={`/detail/${pokemon.id}`} className="link" >
                    <Cards className = {style.card}
                      name={pokemon.name}
                      key={pokemon.id}
                      id={pokemon.id}
                      image={pokemon.image}
                      types={pokemon.types}
                    />
                  </Link>
                </div>
              })} </div>
              
              : <div className={style.loadingContainer}> <Loading className={style.loading} /> </div>}
              
        </div>
        <div className={style.handlePageContainer} >
          <button disabled={currentPage === 0} onClick={handlePrevClick} className={style.handlePageButton}>
           {"<"}
          </button >
          {Array.from({ length: pageCount }).map((_, index) => (
            <button
              className={style.handlePageButton}
              key={index}
              disabled={currentPage === index}
              onClick={() => setCurrentPage(index)}
            >
              {index + 1}
            </button>
          ))}
          <button disabled={currentPage === pageCount - 1} onClick={handleNextClick} className={style.handlePageButton}>
            {">"}
          </button>
  
        </div>
      </div>
    );
}


export default ContainerCards;
